import React, { useState } from "react";

import "./index.css";

export const App = () => {
  const date = new Date();

  let [currDate, fetchDate] = useState(date.toLocaleTimeString('en-US'));
  let dateFun = () => {
    fetchDate(date.toLocaleTimeString('en-GB'))
  }
 
  return (
    <>
      <h1 className="heading">{currDate}</h1>
      <button onClick={dateFun}> Get latest time! </button>
    </>
  );
};
