import React, { useState } from "react";

import "./index.css";
export const App = () => {
  const teal = "rgb(165, 241, 216)";
  const title = "Click Me"
  const [color, setColor] = useState(teal);
  const [btnTitle, setBtnTitle] = useState(title)
  const bgChange = () => {
    let newColor = 'blue'
    let colorArr = ['#FF5733', '#29D03C', '#14806A', '#278AEE', '#1532DA', '#BA15DA', '#DA15A4' ]
    let newBtnTitle = 'YAAY!'
    setColor(newColor);
    setBtnTitle(newBtnTitle);
  };

  const doubleBg = () => {
    setColor(teal);
    setBtnTitle(title);
  };
  return (
    <>
      <div
        className=""
        style={{
          backgroundColor: color,
        }}
      >
        <button onClick={bgChange} onDoubleClick={doubleBg}>{btnTitle}</button>
      </div>
    </>
  );
};
