import React, { useState } from "react";

import "./index.css";

export const App = () => {
  let date = new Date();

  let [currDate, fetchDate] = useState();
  let dateFun = () => {
    fetchDate(date.toLocaleTimeString('en-GB'))
  }
  let dateFun1 = () => {
    fetchDate(date.toLocaleTimeString('en-US'))
  }
  return (
    <>
      <h1 className="heading">{currDate}</h1>
      <button onClick={dateFun}> Get latest time! (24 Hours Format) </button>
      <button onClick={dateFun1}> Get latest time! (12 Hours Format) </button>

    </>
  );
};
