import React, { useState } from "react";
import "./index.css";
import { colors, getColor } from "./colors";
import { CopyToClipboard } from "react-copy-to-clipboard";
import { HiClipboardCopy } from "react-icons/hi";
export const App = () => {
  const [color, setColor] = useState("white");
  const [title, setTitle] = useState("teal");
  console.log(getColor());
  const randomNumber = Math.floor(Math.random() * colors.length);
  let currObj = colors[randomNumber];
  const colorName = currObj.name;
  const hexCode = currObj.hex;

  const bgChange = () => {
    setTitle(colorName);
    setColor(hexCode);
    console.log(getColor());
  };
  return (
    <>
      <div
        style={{
          backgroundColor: color,
        }}
      >
        <button onClick={bgChange}>{color}</button>
        <CopyToClipboard text={hexCode}>
          <HiClipboardCopy
            style={{
              marginLeft: "10px",
              width: "55px",
              height: "55px",
              cursor: "pointer",
              border: "2px solid rgb(35, 35, 44)",
            }}
          />
        </CopyToClipboard>
        <p>{currObj.name}</p>
      </div>
    </>
  );
};
