import "./index.css";
export const App = () => {
  const teal = "rgb(165, 241, 216)";
  const [color, setColor] = useState(teal);
  const bgChange = () => {
    let newColor = 'blue'
    let colorArr = ['#FF5733', '#29D03C', '#14806A', '#278AEE', '#1532DA', '#BA15DA', '#DA15A4', '#DA154E' ]
    setColor(newColor);
  };

  const doubleBg = () => {
    setColor(teal);
    setBtnTitle();
  };
  return (
    <>
      <div
        className=""
        style={{
          backgroundColor: color,
        }}
      >
        <button onClick={bgChange} onDoubleClick={doubleBg}>{btnTitle}</button>
      </div>
    </>
  );
};
