import React, { useState } from "react";

import "./index.css";

export const App = () => {

  let time = new Date().toLocaleTimeString('en-GB');
  const [cTime, setTime] = useState(time);
  const UpdateTime = () => {
    setTime(time)
  }
  setInterval(UpdateTime, 1000)

  return (
    <>
    <h1 className="heading">{cTime}</h1>
    </>
  )
}
