import React, { useState } from "react";
import "./index.css";

export const App = () => {
  const teal = "#a5f1d8";
  const [color, setColor] = useState(teal);
  const [title, setTitle] = useState(teal);
  const colorArr = [
    "#FF5733",
    "#29D03C",
    "#14806A",
    "#278AEE",
    "#1532DA",
    "#BA15DA",
    "#DA15A4",
    "#DA154E",
  ];
  const randomNumber = Math.floor(Math.random() * colorArr.length);

  const bgChange = () => {
    setTitle(colorArr[randomNumber]);
    setColor(colorArr[randomNumber]);
  };
  return (
    <>
      <div
        style={{
          backgroundColor: color,
        }}
      >
        <button onClick={bgChange}>{title}</button>
      </div>
    </>
  );
};
