import React, { useState } from "react";

import "./index.css";
export const App = () => {
  const teal = "rgb(165, 241, 216)";
  const [color, setColor] = useState(teal);
  const bgChange = () => {
    let colorArr = ['#FF5733', '#29D03C', '#14806A', '#278AEE', '#1532DA', '#BA15DA', '#DA15A4', '#DA154E' ]
    let randomNumber = Math.floor(Math.random() * colorArr.length) 
    setColor(colorArr[randomNumber]);
  };

  const bgNormal = () => {
    setColor(colorArr[randomNumber]);
  };
  return (
    <>
      <div
        style={{
          backgroundColor: color,
        }}
      >
        <button onMouseEnter={bgChange} onMouseLeave={bgNormal}>Change the background!</button>
      </div>
    </>
  );
};
