import React, { useState } from "react";
import "./index.css";
import colors from "./colors";
import { CopyToClipboard } from "react-copy-to-clipboard";
import { HiClipboardCopy } from "react-icons/hi";
export const App = () => {
  const teal = "#a5f1d8";
  const [color, setColor] = useState(teal);
  const [title, setTitle] = useState(teal);

  const randomNumber = Math.floor(Math.random() * colors.length);
  
  const bgChange = () => {
    setTitle(colors[randomNumber].name);
    setColor(colors[randomNumber].hex);
    console.log(randomNumber)
  };
  return (
    <>
      <div
        style={{
          backgroundColor: color,
        }}
      >
        <button onClick={bgChange}>{color}</button>
        <CopyToClipboard text={colors[randomNumber].hex}>
          <HiClipboardCopy style={{
            padding: '30px'
          }}/>
        </CopyToClipboard>
      </div>
    </>
  );
};
