import React, { useState } from "react";
import "./index.css";
import colors from "./colors";
import { CopyToClipboard } from "react-copy-to-clipboard";
import { HiClipboardCopy } from "react-icons/hi";
export const App = () => {
  const teal = "#a5f1d8";
  const [color, setColor] = useState(teal);
  const [title, setTitle] = useState(teal);

  const randomNumber = Math.floor(Math.random() * colors.length);
  const newRandom = randomNumber - 1;
  let currObj = colors[randomNumber]
  
  const bgChange = () => {
    setTitle(currObj.name);
    setColor(currObj.hex);
    console.log(currObj)
  };
  return (
    <>
      <div
        style={{
          backgroundColor: color,
        }}
      >
      
        <button onClick={bgChange}>{color}</button>
        <CopyToClipboard text={colors[newRandom]}>
          <HiClipboardCopy style={{
            marginLeft: '10px',
            width: '55px',
            height: '55px',
            cursor: 'pointer',
            border: '2px solid rgb(35, 35, 44)'
          }}/>
        </CopyToClipboard>
      
      </div>
    </>
  );
};
