import React from "react";
import './index.css'
import './seriesInfo'
import { series } from "./seriesInfo";
export function Card(props) {
  return (
      <div className="cards">
        <div className="card">
          <img src={props.imgsrc} alt="show-img" className="card-img" />
          <div className="card-info">
            <span className="category">{props.title}</span>
            <h3 className="card-title">{props.Sname}</h3>
            <a href={props.link} target="_blank" rel="noreferrer">
              <button>Watch Now</button>
            </a>
          </div>
        </div>
      </div>
  );
}


export function makeCard(perCard){
  series.map(perCard => {
    return (
      <Card
            title={perCard.title}
            Sname={perCard.Sname}
            imgsrc={perCard.imgsrc}
            link={perCard.link}
            />
    );
  })
}
