import React, { useState } from "react";
import "./index.css";
import { colors, getColor } from "./colors";
import { CopyToClipboard } from "react-copy-to-clipboard";
import { HiClipboardCopy } from "react-icons/hi";
export const App = () => {
  const [color, setColor] = useState("white");
  const [title, setTitle] = useState("teal");
  const [colorHex, colorName] = getColor();
console.log(colorHex, colorName);
  const bgChange = () => {
    setColor(colorHex);
    setTitle()
    console.log(getColor());
  };
  return (
    <>
      <div
        style={{
          backgroundColor: color,
        }}
      >
        <button onClick={bgChange}>{color}</button>
        <CopyToClipboard text={'hey'}>
          <HiClipboardCopy
            style={{
              marginLeft: "10px",
              width: "55px",
              height: "55px",
              cursor: "pointer",
              border: "2px solid rgb(35, 35, 44)",
            }}
          />
        </CopyToClipboard>
        <p>{'name'}</p>
      </div>
    </>
  );
};
